import React, { useEffect, useState } from "react";
import axios from "axios";
import TextTruncate from 'react-text-truncate';
const ACTIONS = ["View", "Reply"];

const FormatAsCurrency = (int) => {  
    const formatter = new Intl.NumberFormat('en-AU', {
    style: 'currency',
    currency: 'AUD'
  }).format(int.price);
   // var formatter = '$' + int.toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')
    return (<div>{formatter}</div>)
};

const Listing = (props) => {
    // TODO
    console.log('props in listing',props.data)
    // This should be the component which renders an individual listing to the page

    return (<div className="row">
        {props.data && props.data.length? props.data.map((image, index) => (

            <div className="col-md-4">
        <div  key='title${index}'><TextTruncate
    line={1}
    element="span"
    truncateText="â€¦"
    text={image.title}

/></div>
        <FormatAsCurrency price={image.price}></FormatAsCurrency>
            <img
              key='img${index}'              
              src={image.imgUrl}
              alt="Image"
            />
            <div>{image.description}</div>
            </div>

          )):(<div>not found</div>)}
    </div>);
};

const Listings = (props) => {
    console.log('props',props)
    const [searchList, setSearchList] = useState([]);
    // TODO
    // This component should make a request to the api endpoint (props.dataEndpoint)
    // then render the result as set of listings as per the design mocks
    // check props passed in from parent for other values that you may need to use
    useEffect(() => {
        //apiCall();
        axios.get(props.dataEndpoint).then((resp) => {
            //console.log(resp.data);
          setSearchList(resp.data);
          console.log(resp.data);
         console.log('searchList',searchList);
          })
          .catch((err) => {
            console.log("err", err.message);
          });

      }, []);
    return (
        <div>
            <div className="listings__header">Search Results
            <div>{searchList.length} results for Ferrari in Australia</div></div>
            <div className="listings__grid"><Listing data={searchList}></Listing></div>
        </div>
    );
};

export default Listings;
